        m_server.set_open_handler(bind(&print_server::on_open, this, ::_1));
        m_server.set_close_handler(bind(&print_server::on_close, this, ::_1));
        m_server.set_message_handler(bind(&print_server::on_message, this, ::_1, ::_2));
    }

    void on_open(websocketpp::connection_hdl hdl) {
        connection_data data;
        data.session_id = m_next_sessionid++;
        m_connections[hdl] = data;
        std::cout << "Connection opened: Session ID " << data.session_id << std::endl;
    }

    void on_close(websocketpp::connection_hdl hdl) {
        auto it = m_connections.find(hdl);
        if (it != m_connections.end()) {
            std::cout << "Connection closed: Session ID " << it->second.session_id << std::endl;
            m_connections.erase(it);
        }
    }

    void on_message(websocketpp::connection_hdl hdl, websocketpp::server<websocketpp::config::asio>::message_ptr msg) {
        auto& data = get_data_from_hdl(hdl);
        if (msg->get_payload().find("name:") == 0) {
            data.name = msg->get_payload().substr(5);
            std::cout << "Client set name: " << data.name << std::endl;
        } else {
            std::cout << "Received message from Session ID " << data.session_id << ": " << msg->get_payload() << std::endl;
        }
    }

    connection_data& get_data_from_hdl(websocketpp::connection_hdl hdl) {
        auto it = m_connections.find(hdl);
