typedef websocketpp::server<websocketpp::config::asio> server_plain;
typedef websocketpp::server<websocketpp::config::asio_tls> server_tls;
using websocketpp::lib::placeholders::_1;
using websocketpp::lib::placeholders::_2;
using websocketpp::lib::bind;
typedef websocketpp::lib::shared_ptr<boost::asio::ssl::context> context_ptr;

class WebSocketServer {
public:
    WebSocketServer() : ios_(), endpoint_plain_(), endpoint_tls_() {}

    void init() {
        endpoint_plain_.init_asio(&ios_);
        endpoint_plain_.set_message_handler(
            bind(&WebSocketServer::on_message<server_plain>, this, &endpoint_plain_, ::_1, ::_2));
        endpoint_plain_.listen(80);
        endpoint_plain_.start_accept();

        endpoint_tls_.init_asio(&ios_);
        endpoint_tls_.set_message_handler(
            bind(&WebSocketServer::on_message<server_tls>, this, &endpoint_tls_, ::_1, ::_2));
        endpoint_tls_.set_tls_init_handler(bind(&WebSocketServer::on_tls_init, this, ::_1));
        endpoint_tls_.listen(443);
        endpoint_tls_.start_accept();
    }

    void run() {
        ios_.run();
    }

private:
    template <typename EndpointType>
    void on_message(EndpointType* s, websocketpp::connection_hdl hdl,
        typename EndpointType::message_ptr msg)
    {
        std::cout << "on_message called with hdl: " << hdl.lock().get()
                << " and message: " << msg->get_payload()
                << std::endl;

        try {
            s->send(hdl, msg->get_payload(), msg->get_opcode());
        } catch (websocketpp::exception const & e) {
            std::cout << "Echo failed because: "
                    << "(" << e.what() << ")" << std::endl;
        }
    }

    std::string get_password() {
        return "test";
    }

    context_ptr on_tls_init(websocketpp::connection_hdl hdl) {
        std::cout << "on_tls_init called with hdl: " << hdl.lock().get() << std::endl;
        context_ptr ctx(new boost::asio::ssl::context(boost::asio::ssl::context::tlsv1));

        try {
            ctx->set_options(boost::asio::ssl::context::default_workarounds |
                            boost::asio::ssl::context::no_sslv2 |
                            boost::asio::ssl::context::no_sslv3 |
                            boost::asio::ssl::context::single_dh_use);
            ctx->set_password_callback(bind(&WebSocketServer::get_password, this));
            ctx->use_certificate_chain_file("server.pem");
            ctx->use_private_key_file("server.pem", boost::asio::ssl::context::pem);
        } catch (std::exception& e) {
            std::cout << e.what() << std::endl;
        }
        return ctx;
    }

    boost::asio::io_service ios_;
    server_plain endpoint_plain_;
    server_tls endpoint_tls_;
};

