namespace websocket = beast::websocket;
namespace net = boost::asio;
namespace ssl = boost::asio::ssl;
using tcp = boost::asio::ip::tcp;
using udp = boost::asio::ip::udp;
using json = nlohmann::json;

class WebOsClient::Impl : public std::enable_shared_from_this<WebOsClient::Impl> {
private:
    net::io_context& ioc_;
    ssl::context& ctx_;
    Device device_settings_;
    std::shared_ptr<Logging> log_;
    tcp::resolver resolver_;
    std::optional<websocket::stream<beast::ssl_stream<beast::tcp_stream>>> ws_;
    std::optional<websocket::stream<beast::tcp_stream>> ws_tcp_;
    beast::flat_buffer buffer_;
    std::string host_;
    int socket_status_ = SOCKET_DISCONNECTED;

    void run() {
        socket_status_ = SOCKET_CONNECTING;
        if(device_settings_.ssl)
            ws_.emplace(resolver_.get_executor(), ctx_);
        else
            ws_tcp_.emplace(resolver_.get_executor());
        host_ = device_settings_.ip;
        resolver_.async_resolve(host_.c_str(), device_settings_.ssl ? PORT_SSL : PORT,
            beast::bind_front_handler(&Impl::onResolve, shared_from_this()));
    }

    void onResolve(beast::error_code ec, tcp::resolver::results_type results) {
        if (ec) return onError(ec, "onResolve");
        socket_status_ = SOCKET_CONNECTING;
