class WebOsClient::Impl {
public:
    Impl(net::io_context& ioc, ssl::context& ctx, Device& device_settings, std::shared_ptr<Logging> log)
        : ioc_(ioc), ctx_(ctx), device_settings_(device_settings), log_(log),
          resolver_(ioc), socket_status_(0) {}

    void run() {
        host_ = device_settings_.host + ":" + std::to_string(device_settings_.port);
        resolver_.async_resolve(device_settings_.host, std::to_string(device_settings_.port),
            beast::bind_front_handler(&WebOsClient::Impl::onResolve, this));
    }

    void onResolve(beast::error_code ec, tcp::resolver::results_type results) {
        if (ec) {
            onError(ec, "Resolve");
            return;
        }
        beast::get_lowest_layer(ws_tcp_ ? *ws_tcp_ : *ws_).expires_after(std::chrono::seconds(30));
        beast::get_lowest_layer(ws_tcp_ ? *ws_tcp_ : *ws_).async_connect(results,
            beast::bind_front_handler(&WebOsClient::Impl::onConnect, this));
    }

    void onConnect(beast::error_code ec, tcp::resolver::results_type::endpoint_type endpoint) {
        if (ec) {
            onError(ec, "Connect");
            return;
        }
        host_ = endpoint.address().to_string() + ":" + std::to_string(endpoint.port());
        if (ws_) {
            ws_->next_layer().async_handshake(ssl::stream_base::client,
                beast::bind_front_handler(&WebOsClient::Impl::onSSLhandshake, this));
        } else {
            ws_tcp_->async_handshake(host_, "/", beast::bind_front_handler(&WebOsClient::Impl::onWinsockHandshake, this));
        }
    }

    void onSSLhandshake(beast::error_code ec) {
        if (ec) {
            onError(ec, "SSL Handshake");
            return;
        }
        ws_->async_handshake(host_, "/", beast::bind_front_handler(&WebOsClient::Impl::onWinsockHandshake, this));
    }

    void onWinsockHandshake(beast::error_code ec) {
        if (ec) {
            onError(ec, "WebSocket Handshake");
            return;
        }
        socket_status_ = 1; // Connected
        read();
    }

    void send(std::string message, std::string type) {
        if (socket_status_ != 1) return;
        auto& ws_stream = ws_ ? *ws_ : *ws_tcp_;
        ws_stream.async_write(net::buffer(message),
            beast::bind_front_handler(&WebOsClient::Impl::onWrite, this));
    }

    void onWrite(beast::error_code ec, std::size_t bytes_transferred) {
        if (ec) {
            onError(ec, "Write");
            return;
        }
    }

    void read() {
        auto& ws_stream = ws_ ? *ws_ : *ws_tcp_;
        ws_stream.async_read(buffer_,
            beast::bind_front_handler(&WebOsClient::Impl::onRead, this));
    }

    void onRead(beast::error_code ec, std::size_t bytes_transferred) {
        if (ec) {
            onError(ec, "Read");
            return;
        }
        buffer_.consume(bytes_transferred);
        read();
    }

    void onError(beast::error_code& ec, std::string where) {
        log_->error("Error in " + where + ": " + ec.message());
        close();
    }

    void enqueueWork(Work& work) {
        // Implementation for work queue
    }

    void close() {
        if (ws_) {
            ws_->async_close(websocket::close_code::normal,
                beast::bind_front_handler(&WebOsClient::Impl::onClose, this));
        } else if (ws_tcp_) {
            ws_tcp_->async_close(websocket::close_code::normal,
                beast::bind_front_handler(&WebOsClient::Impl::onClose, this));
        }
    }

private:
    net::io_context& ioc_;
    ssl::context& ctx_;
    Device& device_settings_;
    std::shared_ptr<Logging> log_;
    tcp::resolver resolver_;
    std::optional<websocket::stream<beast::ssl_stream<beast::tcp_stream>>> ws_;
    std::optional<websocket::stream<beast::tcp_stream>> ws_tcp_;
    beast::flat_buffer buffer_;
    std::string host_;
    int socket_status_;
};
