#include <cstring>
#include <arpa/inet.h>
#include <unistd.h>
#include <netinet/in.h>
#include <sys/socket.h>
#include <sys/types.h>

class LinHttpHandler {
public:
    class SocketCloser {
    public:
        explicit SocketCloser(int sockFd) : sockFd(sockFd) {}
        ~SocketCloser() {
            if (sockFd != -1) {
                close(sockFd);
            }
        }
    private:
        int sockFd;
    };

    std::string send(const std::string& msg, const std::string& adr, int port) {
        int sockFd = socket(AF_INET, SOCK_STREAM, 0);
        if (sockFd == -1) {
            throw std::system_error(errno, std::generic_category(), "Failed to create socket");
        }
        SocketCloser closer(sockFd);

        sockaddr_in serverAddr;
        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(port);
        if (inet_pton(AF_INET, adr.c_str(), &serverAddr.sin_addr) <= 0) {
            throw std::system_error(errno, std::generic_category(), "Invalid address");
        }

        if (connect(sockFd, (struct sockaddr*)&serverAddr, sizeof(serverAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Connection failed");
        }

        if (send(sockFd, msg.c_str(), msg.size(), 0) < 0) {
            throw std::system_error(errno, std::generic_category(), "Send failed");
        }

        char buffer[1024] = {0};
        int bytesReceived = recv(sockFd, buffer, sizeof(buffer) - 1, 0);
        if (bytesReceived < 0) {
            throw std::system_error(errno, std::generic_category(), "Receive failed");
        }

        return std::string(buffer, bytesReceived);
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sockFd = socket(AF_INET, SOCK_DGRAM, 0);
        if (sockFd == -1) {
            throw std::system_error(errno, std::generic_category(), "Failed to create socket");
        }
        SocketCloser closer(sockFd);

        sockaddr_in multicastAddr;
        multicastAddr.sin_family = AF_INET;
        multicastAddr.sin_port = htons(port);
        if (inet_pton(AF_INET, adr.c_str(), &multicastAddr.sin_addr) <= 0) {
            throw std::system_error(errno, std::generic_category(), "Invalid address");
        }

        if (sendto(sockFd, msg.c_str(), msg.size(), 0, (struct sockaddr*)&multicastAddr, sizeof(multicastAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Send failed");
        }

        std::vector<std::string> responses;
        auto endTime = std::chrono::steady_clock::now() + timeout;
        while (std::chrono::steady_clock::now() < endTime) {
            char buffer[1024] = {0};
            sockaddr_in senderAddr;
            socklen_t senderAddrLen = sizeof(senderAddr);
            int bytesReceived = recvfrom(sockFd, buffer, sizeof(buffer) - 1, MSG_DONTWAIT, (struct sockaddr*)&senderAddr, &senderAddrLen);
            if (bytesReceived > 0) {
                responses.emplace_back(buffer, bytesReceived);
            }
        }

        return responses;
    }
};
