            if (select_res == 1) {
                int so_error;
                socklen_t len = sizeof so_error;

                getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &so_error, &len);

                if (so_error == 0) {
                    select_good = true;
                }
            }
            if (!select_good) {
                return false;
            }
#endif
        } else if (res == -1) {
            return false;
        }
        return true;
    }

