        memset(&hints, 0, sizeof(hints));
        hints.ai_family = AF_UNSPEC;
        hints.ai_socktype = SOCK_STREAM;

        if (getaddrinfo(address.c_str(), std::to_string(port).c_str(), &hints, &res) != 0) {
            return false;
        }

        main_socket = socket(res->ai_family, res->ai_socktype, res->ai_protocol);
        if (main_socket == -1) {
            freeaddrinfo(res);
            return false;
        }

        if (!ConnectWithTimeout(main_socket, res->ai_addr, res->ai_addrlen, 5)) {
            freeaddrinfo(res);
            return false;
        }

        connected = true;
        freeaddrinfo(res);
        return true;
    }

    bool Read(void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected) return false;
        fd_set read_fds;
        FD_ZERO(&read_fds);
        FD_SET(main_socket, &read_fds);

        struct timeval tv;
        tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(timeout).count();
        tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout).count() % 1000000;

        int result = select(main_socket + 1, &read_fds, nullptr, nullptr, &tv);
        if (result <= 0) return false;

        ssize_t bytes_read = recv(main_socket, static_cast<char*>(buffer), size, 0);
        return bytes_read == static_cast<ssize_t>(size);
    }

    bool Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!connected) return false;
        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(main_socket, &write_fds);

        struct timeval tv;
        tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(timeout).count();
        tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout).count() % 1000000;

        int result = select(main_socket + 1, nullptr, &write_fds, nullptr, &tv);
        if (result <= 0) return false;

        ssize_t bytes_written = send(main_socket, static_cast<const char*>(buffer), size, 0);
        return bytes_written == static_cast<ssize_t>(size);
    }

    void Disconnect() {
        if (connected) {
#ifdef _WIN32
            closesocket(main_socket);
#else
            close(main_socket);
#endif
            connected = false;
        }
