        if (result <= 0) return false;

        int optval;
        socklen_t optlen = sizeof(optval);
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, reinterpret_cast<char*>(&optval), &optlen) < 0 || optval != 0) {
            return false;
        }

        SetNonBlock(sockFD, false);
        return true;
    }

private:
    std::string address;
    u16 port;
    SocketHolder main_socket;
    bool connected;
};

