    unsigned short port;
} stEndPoint;

void *readwrite_routine(void *arg) {
    stEndPoint *endpoint = (stEndPoint *)arg;
    int sockfd;
    struct sockaddr_in server_addr;
    char buffer[1024];
    const char *message = "Hello, Server!";
    int flags, n;

    while (1) {
        sockfd = socket(AF_INET, SOCK_STREAM, 0);
        if (sockfd < 0) {
            perror("Socket creation failed");
            sleep(1);
            continue;
        }

        memset(&server_addr, 0, sizeof(server_addr));
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(endpoint->port);

        if (endpoint->ip == NULL || strcmp(endpoint->ip, "0.0.0.0") == 0) {
            server_addr.sin_addr.s_addr = htonl(INADDR_ANY);
        } else {
            if (inet_pton(AF_INET, endpoint->ip, &server_addr.sin_addr) <= 0) {
                perror("Invalid IP address");
                close(sockfd);
                sleep(1);
                continue;
            }
        }

        flags = fcntl(sockfd, F_GETFL, 0);
        fcntl(sockfd, F_SETFL, flags | O_NONBLOCK);

        if (connect(sockfd, (struct sockaddr *)&server_addr, sizeof(server_addr)) < 0) {
            if (errno != EINPROGRESS) {
                perror("Connection failed");
                close(sockfd);
                sleep(1);
                continue;
            }
        }

        fd_set write_fds;
        struct timeval timeout;
        FD_ZERO(&write_fds);
        FD_SET(sockfd, &write_fds);
        timeout.tv_sec = 5;
        timeout.tv_usec = 0;

        if (select(sockfd + 1, NULL, &write_fds, NULL, &timeout) <= 0) {
            perror("Connection timeout or error");
            close(sockfd);
            sleep(1);
            continue;
        }

        while (1) {
            n = send(sockfd, message, strlen(message), 0);
            if (n < 0) {
                perror("Send failed");
                break;
            }

            n = recv(sockfd, buffer, sizeof(buffer) - 1, 0);
            if (n < 0) {
                perror("Receive failed");
                break;
            } else if (n == 0) {
                printf("Server closed connection\n");
                break;
            }

            buffer[n] = '\0';
            printf("Received: %s\n", buffer);
        }

        close(sockfd);
        sleep(1);
    }

    return NULL;
}

