class DownloadTask {
public:
    static void initialize() {
        curl_global_init(CURL_GLOBAL_DEFAULT);
    }

    static void uninitialize() {
        curl_global_cleanup();
    }

    DownloadTask(const std::string& url, const std::string& output, const std::string& defaultFile, time_t mtime, const std::string& name = "")
        : m_url(url), m_output(output), m_default(defaultFile), m_mtime(mtime), m_name(name), m_retries(0) {
        m_outputTmp = m_output + ".tmp";
        m_userAgent = "libcurl-agent/1.0";
    }

    bool run() {
        if (downloadFile(m_url) || (!m_urlBackup.empty() && downloadFile(m_urlBackup))) {
            std::rename(m_outputTmp.c_str(), m_output.c_str());
            std::remove(m_outputTmp.c_str());
            return true;
        }
        std::rename(m_default.c_str(), m_output.c_str());
        return false;
    }

    unsigned int getRetries() const {
        return m_retries;
    }

private:
    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;

    static size_t writeData(void* ptr, size_t size, size_t nmemb, void* stream) {
        std::ofstream* out = static_cast<std::ofstream*>(stream);
        out->write(static_cast<char*>(ptr), size * nmemb);
        return size * nmemb;
    }

    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) {
            m_error = "Failed to initialize CURL";
            return false;
        }

        std::ofstream outFile(m_outputTmp, std::ios::binary);
        if (!outFile.is_open()) {
            m_error = "Failed to open temporary file for writing";
            curl_easy_cleanup(curl);
            return false;
        }

        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, &outFile);
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());

        CURLcode res;
        for (m_retries = 0; m_retries < 3; ++m_retries) {
            res = curl_easy_perform(curl);
            if (res == CURLE_OK) {
                curl_easy_cleanup(curl);
                outFile.close();
                return true;
            }
        }

        m_error = curl_easy_strerror(res);
        curl_easy_cleanup(curl);
        outFile.close();
        return false;
    }
};

