class HttpWebResponse {
public:
    std::string content;
    long statusCode;
    std::map<std::string, std::string> headers;
};

class HttpWebRequest {
private:
    std::string uri_;
    std::string method_;
    std::map<std::string, std::string> header_;
    std::stringstream is_;

    static size_t callback(void *buffer, size_t size, size_t nmemb, void *data) {
        std::stringstream *stream = static_cast<std::stringstream*>(data);
        size_t totalSize = size * nmemb;
        stream->write(static_cast<const char*>(buffer), totalSize);
        return totalSize;
    }

    HttpWebResponse* GetFileResponse(const char *uri) {
        std::ifstream file(uri + 7); // Skip "file://"
        if (!file.is_open()) {
            return nullptr;
        }
        std::stringstream buffer;
        buffer << file.rdbuf();
        HttpWebResponse *response = new HttpWebResponse();
        response->content = buffer.str();
        response->statusCode = 200;
        return response;
    }

    HttpWebResponse* GetNetResponse(const char *uri) {
        CURL *curl;
        CURLcode res;
        curl = curl_easy_init();
        if (!curl) {
            return nullptr;
        }

        HttpWebResponse *response = new HttpWebResponse();
        curl_easy_setopt(curl, CURLOPT_URL, uri);
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, callback);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, &is_);

        struct curl_slist *headers = nullptr;
        for (const auto &header : header_) {
            std::string headerString = header.first + ": " + header.second;
            headers = curl_slist_append(headers, headerString.c_str());
        }
        if (headers) {
            curl_easy_setopt(curl, CURLOPT_HTTPHEADER, headers);
        }

        res = curl_easy_perform(curl);
        if (res != CURLE_OK) {
            delete response;
            response = nullptr;
        } else {
            curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &response->statusCode);
            response->content = is_.str();
        }

        curl_slist_free_all(headers);
        curl_easy_cleanup(curl);
        return response;
    }

public:
    HttpWebRequest(std::string &uri) : uri_(uri), method_("GET") {}

    HttpWebResponse* GetResponse() {
        if (uri_.substr(0, 7) == "file://") {
            return GetFileResponse(uri_.c_str());
        } else {
            return GetNetResponse(uri_.c_str());
        }
    }
};

