void pipe_communication() {
    int pipefd[2];
    pid_t pid;
    const char* data = "Hello from parent";
    char buffer[128];

    if (pipe(pipefd) == -1) {
        std::cerr << "pipe create error" << std::endl;
        return;
    }

    pid = fork();
    if (pid == -1) {
        std::cerr << "fork error" << std::endl;
        return;
    }

    if (pid == 0) { // Child process
        close(pipefd[1]); // Close unused write end
        ssize_t bytesRead = read(pipefd[0], buffer, sizeof(buffer) - 1);
        if (bytesRead > 0) {
            buffer[bytesRead] = '\0';
            std::cout << "Read data from the pipe is [" << buffer << "]" << std::endl;
        }
        close(pipefd[0]);
    } else { // Parent process
        close(pipefd[0]); // Close unused read end
        std::cout << "Write data [" << data << "]" << std::endl;
        write(pipefd[1], data, strlen(data));
        std::cout << "Parent write over" << std::endl;
        close(pipefd[1]);
        std::cout << "parent close fd[1] over" << std::endl;
        wait(NULL); // Wait for child process to finish
    }
}

