#include <boost/asio.hpp>
#include <iostream>
#include <deque>

using boost::asio::ip::tcp;

class chat_message {
public:
    enum { header_length = 4 };
    enum { max_body_length = 512 };

    chat_message() : body_length_(0) {}

    const char* data() const { return data_; }
    char* data() { return data_; }
    std::size_t length() const { return header_length + body_length_; }

    const char* body() const { return data_ + header_length; }
    char* body() { return data_ + header_length; }
    std::size_t body_length() const { return body_length_; }
    void body_length(std::size_t new_length) {
        body_length_ = new_length;
        if (body_length_ > max_body_length)
            body_length_ = max_body_length;
    }

    bool decode_header() {
        char header[header_length + 1] = "";
        std::strncat(header, data_, header_length);
        body_length_ = std::atoi(header);
        if (body_length_ > max_body_length) {
            body_length_ = 0;
            return false;
        }
        return true;
    }

    void encode_header() {
        char header[header_length + 1] = "";
        std::sprintf(header, "%4d", static_cast<int>(body_length_));
        std::memcpy(data_, header, header_length);
    }

private:
    char data_[header_length + max_body_length];
    std::size_t body_length_;
};

class posix_chat_client {
public:
    posix_chat_client(boost::asio::io_context& io_context, const tcp::resolver::results_type& endpoints)
        : io_context_(io_context),
          socket_(io_context),
          input_(io_context, ::dup(STDIN_FILENO)),
          output_(io_context, ::dup(STDOUT_FILENO)) {
        do_connect(endpoints);
        do_read_input();
    }

    void close() {
        boost::asio::post(io_context_, [this]() { socket_.close(); });
    }

private:
    void do_connect(const tcp::resolver::results_type& endpoints) {
        boost::asio::async_connect(socket_, endpoints,
            [this](boost::system::error_code ec, tcp::endpoint) {
                if (!ec) {
                    do_read_header();
                }
            });
    }

    void do_read_header() {
        boost::asio::async_read(socket_,
            boost::asio::buffer(read_msg_.data(), chat_message::header_length),
            [this](boost::system::error_code ec, std::size_t /*length*/) {
                if (!ec && read_msg_.decode_header()) {
                    do_read_body();
                } else {
                    socket_.close();
                }
            });
    }

    void do_read_body() {
        boost::asio::async_read(socket_,
            boost::asio::buffer(read_msg_.body(), read_msg_.body_length()),
            [this](boost::system::error_code ec, std::size_t /*length*/) {
                if (!ec) {
                    do_write_output();
                } else {
                    socket_.close();
                }
            });
    }

    void do_write_output() {
        boost::asio::async_write(output_,
            boost::asio::buffer(read_msg_.body(), read_msg_.body_length()),
