    {
        int err;
        struct addrinfo hints, *res, *result;

        char port_str[6];
        snprintf(port_str, 6, "%d", port_num);

        socket_count = 0;

#ifdef WIN32
        if(WSAStartup(MAKEWORD(2, 2), &wsa) != NO_ERROR)
