long APIRequest(const std::string& method, const std::string& location, const std::string& URI, json* request_data, json* response_data) {
    httplib::Client cli(location);
    httplib::Result res;

    if (method == "GET") {
        res = cli.Get(URI.c_str());
    } else if (method == "PUT") {
        if (request_data) {
            res = cli.Put(URI.c_str(), request_data->dump(), "application/json");
        } else {
            res = cli.Put(URI.c_str());
        }
    } else if (method == "DELETE") {
        res = cli.Delete(URI.c_str());
    } else if (method == "POST") {
        if (request_data) {
            res = cli.Post(URI.c_str(), request_data->dump(), "application/json");
        } else {
            res = cli.Post(URI.c_str());
        }
    } else {
        return -1; // Invalid method
    }

    if (res) {
        long status_code = res->status;
        if (status_code >= 200 && status_code < 300) {
            if (response_data) {
                try {
                    *response_data = json::parse(res->body);
                } catch (const json::parse_error& e) {
                    // Handle JSON parsing error
                    return -2; 
                }
            }
        }
        return status_code;
    } else {
        return -3; // Request failed
    }
}
