#include <netinet/in.h>
#include <arpa/inet.h>
#include <unistd.h>
#include <netdb.h>
#include <chrono>
#include <thread>
#include <system_error>

class LinHttpHandler {
public:
    class SocketCloser {
    public:
        SocketCloser(int sockFd) : sockFd_(sockFd) {}
        ~SocketCloser() { close(sockFd_); }
    private:
        int sockFd_;
    };

    std::string send(const std::string& msg, const std::string& adr, int port) {
        addrinfo hints{}, *result;
        hints.ai_family = AF_INET;
        hints.ai_socktype = SOCK_STREAM;

        if (getaddrinfo(adr.c_str(), std::to_string(port).c_str(), &hints, &result) != 0) {
            throw std::system_error(errno, std::generic_category(), "getaddrinfo failed");
        }

        int sockfd = socket(result->ai_family, result->ai_socktype, result->ai_protocol);
        if (sockfd == -1) {
            freeaddrinfo(result);
            throw std::system_error(errno, std::generic_category(), "socket creation failed");
        }
        SocketCloser closer(sockfd);

        if (connect(sockfd, result->ai_addr, result->ai_addrlen) == -1) {
            freeaddrinfo(result);
            throw std::system_error(errno, std::generic_category(), "connect failed");
        }
        freeaddrinfo(result);

        if (::send(sockfd, msg.c_str(), msg.size(), 0) == -1) {
            throw std::system_error(errno, std::generic_category(), "send failed");
        }

        char buffer[4096];
        std::string response;
        ssize_t bytes_received;
        while ((bytes_received = recv(sockfd, buffer, sizeof(buffer) - 1, 0)) > 0) {
            buffer[bytes_received] = '\0';
            response += buffer;
        }
        if (bytes_received == -1) {
            throw std::system_error(errno, std::generic_category(), "recv failed");
        }
        return response;
    }


    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
         int sockfd = socket(AF_INET, SOCK_DGRAM, 0);
        if (sockfd < 0) {
            throw std::system_error(errno, std::generic_category(), "socket creation failed");
        }
        SocketCloser closer(sockfd);

        int ttl = 1;
        if (setsockopt(sockfd, IPPROTO_IP, IP_MULTICAST_TTL, &ttl, sizeof(ttl)) < 0) {
            throw std::system_error(errno, std::generic_category(), "setsockopt failed");
        }

        sockaddr_in addr{};
        addr.sin_family = AF_INET;
        addr.sin_port = htons(port);
        addr.sin_addr.s_addr = inet_addr(adr.c_str());

        if (sendto(sockfd, msg.c_str(), msg.length(), 0, (sockaddr*)&addr, sizeof(addr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "sendto failed");
        }

        std::vector<std::string> responses;
        auto start_time = std::chrono::steady_clock::now();

        while (std::chrono::steady_clock::now() - start_time < timeout) {
            char buffer[4096];
            sockaddr_in sender_addr;
            socklen_t sender_addr_len = sizeof(sender_addr);
            int bytes_received = recvfrom(sockfd, buffer, sizeof(buffer) - 1, 0, (sockaddr*)&sender_addr, &sender_addr_len);
            if (bytes_received > 0) {
                buffer[bytes_received] = '\0';
                responses.push_back(buffer);
            } else if (bytes_received < 0 && errno != EAGAIN && errno != EWOULDBLOCK) {
                throw std::system_error(errno, std::generic_category(), "recvfrom failed");
            }
        }
        return responses;
    }
};
