typedef int SocketHolder;

class TCPClient {
public:
    std::string address;
    u_short port;
    SocketHolder main_socket;
    bool connected;

    TCPClient(const std::string& addr, u_short p) : address(addr), port(p), main_socket(-1), connected(false) {}

    bool Connect() {
        struct sockaddr_in server_addr;
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(port);
        if (inet_pton(AF_INET, address.c_str(), &server_addr.sin_addr) <= 0) {
            struct hostent *he = gethostbyname(address.c_str());
            if (!he) return false;
            memcpy(&server_addr.sin_addr, he->h_addr_list[0], he->h_length);
        }

        main_socket = socket(AF_INET, SOCK_STREAM, 0);
        if (main_socket < 0) return false;

        if (!ConnectWithTimeout(main_socket, (sockaddr*)&server_addr, sizeof(server_addr), 5)) {
            Disconnect();
            return false;
        }

        connected = true;
        return true;
    }

    bool Read(void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!SetNonBlock(main_socket, true)) return false;

        auto start_time = std::chrono::steady_clock::now();
        size_t bytes_read = 0;
        while (bytes_read < size) {
            fd_set read_fds;
            FD_ZERO(&read_fds);
            FD_SET(main_socket, &read_fds);
            timeval tv;
            tv.tv_sec = 0;
            tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout).count();

            int select_result = select(main_socket + 1, &read_fds, nullptr, nullptr, &tv);
            if (select_result < 0) return false;
            if (select_result == 0) return false;

            int received = recv(main_socket, (char*)buffer + bytes_read, size - bytes_read, 0);
            if (received <= 0) return false;
            bytes_read += received;

            if (std::chrono::steady_clock::now() - start_time > timeout) return false;
        }
        SetNonBlock(main_socket, false);
        return true;
    }

    bool Write(const void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!SetNonBlock(main_socket, true)) return false;
        auto start_time = std::chrono::steady_clock::now();
        size_t bytes_written = 0;

        while (bytes_written < size) {
            fd_set write_fds;
            FD_ZERO(&write_fds);
            FD_SET(main_socket, &write_fds);
            timeval tv;
            tv.tv_sec = 0;
            tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout).count();

            int select_result = select(main_socket + 1, nullptr, &write_fds, nullptr, &tv);
            if (select_result < 0) return false;
            if (select_result == 0) return false;

            int sent = send(main_socket, (const char*)buffer + bytes_written, size - bytes_written, 0);
            if (sent <= 0) return false;
            bytes_written += sent;

            if (std::chrono::steady_clock::now() - start_time > timeout) return false;
        }
        SetNonBlock(main_socket, false);
        return true;
    }

    void Disconnect() {
        if (main_socket >= 0) {
#ifdef _WIN32
            closesocket(main_socket);
#else
            close(main_socket);
#endif
            main_socket = -1;
        }
        connected = false;
    }

    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
#ifdef _WIN32
        u_long mode = nonBlocking ? 1 : 0;
        return ioctlsocket(sockFD, FIONBIO, &mode) == 0;
#else
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) return false;
        if (nonBlocking) flags |= O_NONBLOCK;
        else flags &= ~O_NONBLOCK;
        return fcntl(sockFD, F_SETFL, flags) == 0;
#endif
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        if (!SetNonBlock(sockFD, true)) return false;

        connect(sockFD, (sockaddr*)server_addr, server_addr_len);

        fd_set writefds;
        FD_ZERO(&writefds);
        FD_SET(sockFD, &writefds);

        timeval timeout;
        timeout.tv_sec = timeout_seconds;
        timeout.tv_usec = 0;

        int select_result = select(sockFD + 1, nullptr, &writefds, nullptr, &timeout);

        if (select_result == 1) {
            int so_error;
            socklen_t len = sizeof(so_error);
            getsockopt(sockFD, SOL_SOCKET, SO_ERROR, (char*)&so_error, &len);
            if (so_error == 0) {
                SetNonBlock(sockFD, false);
                return true;
            }
        }
        SetNonBlock(sockFD, false);
        return false;
    }

    ~TCPClient() {
        Disconnect();
    }
};
