class DFNetWork; // Forward declaration

class UDPRecv {
private:
    DFNetWork* m_poDFNetWork;
    int m_iSockFD;
    bool m_bIsEnd;
    bool m_bIsStarted;

public:
    UDPRecv(DFNetWork* poDFNetWork) : m_poDFNetWork(poDFNetWork), m_iSockFD(-1), m_bIsEnd(false), m_bIsStarted(false) {}

    ~UDPRecv() {
        if (m_iSockFD != -1) {
            close(m_iSockFD);
        }
    }

    void Stop() {
        m_bIsEnd = true;
        if (m_bIsStarted) {
            // Ensure the thread has a chance to exit gracefully.  A more robust solution
            // might involve sending a dummy packet to unblock the recvfrom call.
            std::this_thread::sleep_for(std::chrono::milliseconds(100));
        }
    }

    int Init(int iPort) {
        m_iSockFD = socket(AF_INET, SOCK_DGRAM, 0);
        if (m_iSockFD < 0) {
            return -1;
        }

        sockaddr_in stLocalAddr;
        memset(&stLocalAddr, 0, sizeof(stLocalAddr));
        stLocalAddr.sin_family = AF_INET;
        stLocalAddr.sin_port = htons(iPort);
        stLocalAddr.sin_addr.s_addr = INADDR_ANY;

        if (bind(m_iSockFD, (sockaddr*)&stLocalAddr, sizeof(stLocalAddr)) < 0) {
            close(m_iSockFD);
            m_iSockFD = -1;
            return -1;
        }
        return 0;
    }

    void run() {
        if (m_iSockFD == -1) {
            return;
        }

        m_bIsStarted = true;
        char acBuf[1024 * 1024]; // 1MB buffer
        sockaddr_in stFromAddr;
        socklen_t iFromLen = sizeof(stFromAddr);

        while (!m_bIsEnd) {
            int iRecvLen = recvfrom(m_iSockFD, acBuf, sizeof(acBuf), 0, (sockaddr*)&stFromAddr, &iFromLen);
            if (iRecvLen > 0 && m_poDFNetWork) {
                m_poDFNetWork->OnRecvData(acBuf, iRecvLen); // Process received data
            } else if (iRecvLen < 0 && errno != EAGAIN && errno != EWOULDBLOCK) {
                // Handle errors other than non-blocking would block
                break; 
            }
        }
        m_bIsStarted = false;
    }
};
