#include <sys/stat.h>

class DownloadTask {
public:
    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;

    static void initialize() { curl_global_init(CURL_GLOBAL_ALL); }
    static void uninitialize() { curl_global_cleanup(); }

    DownloadTask(const std::string& url, const std::string& output, const std::string& defaultFile, time_t mtime, const std::string& name = "")
        : m_url(url), m_output(output), m_default(defaultFile), m_mtime(mtime), m_retries(0), m_name(name) 
    {
        m_outputTmp = m_output + ".tmp";
    }

    bool run() {
        if (downloadFile(m_url)) return true;
        if (!m_urlBackup.empty() && downloadFile(m_urlBackup)) return true;
        if (!m_default.empty()) {
            std::rename(m_default.c_str(), m_output.c_str());
            struct stat st;
            if (stat(m_default.c_str(), &st) == 0) m_mtime = st.st_mtime;
        }
        return false;
    }

    unsigned int getRetries() const { return m_retries; }

private:
    size_t writeData(void* ptr, size_t size, size_t nmemb) {
        std::ofstream ofs(m_outputTmp, std::ios::binary | std::ios::app);
        ofs.write(static_cast<char*>(ptr), size * nmemb);
        return size * nmemb;
    }

    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) return false;

        std::ofstream ofs(m_outputTmp, std::ios::binary);
        if (!ofs.is_open()) { curl_easy_cleanup(curl); return false; }
        ofs.close();

        FILE* fp = fopen(m_outputTmp.c_str(), "wb");
        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &DownloadTask::writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, this);
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());
        curl_easy_setopt(curl, CURLOPT_FOLLOWLOCATION, 1L);
        curl_easy_setopt(curl, CURLOPT_FAILONERROR, true);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, fp);

        CURLcode res = curl_easy_perform(curl);
        fclose(fp);
        curl_easy_cleanup(curl);

        if (res == CURLE_OK) {
            std::rename(m_outputTmp.c_str(), m_output.c_str());
            struct utimbuf utb;
            utb.actime = m_mtime;
            utb.modtime = m_mtime;
            utime(m_output.c_str(), &utb);
            return true;
        } else {
            m_retries++;
            m_error = curl_easy_strerror(res);
            return false;
        }
    }
};
