#include <iostream>
#include <string>
#include <map>
#include <curl/curl.h>
#include <fstream>
#include <sstream>

class HttpWebResponse {
public:
  std::string data;
  long response_code;
};

class HttpWebRequest {
public:
  std::string uri_;
  std::string method_ = "GET";
  std::map<std::string, std::string> header_;
  std::iostream* is_;

  HttpWebRequest(std::string &uri) : uri_(uri), is_(new std::stringstream) {}

  static size_t callback(void *buffer, size_t size, size_t nmemb, void *data) {
    size_t realsize = size * nmemb;
    std::iostream* strm = (std::iostream*)data;
    strm->write((const char*)buffer, realsize);
    return realsize;
  }

  HttpWebResponse* GetFileResponse(const char *uri) {
    std::ifstream file(uri + 7); // Skip "file:///"
    if (!file.is_open()) return nullptr;
    HttpWebResponse* response = new HttpWebResponse();
    response->data = std::string((std::istreambuf_iterator<char>(file)), std::istreambuf_iterator<char>());
    response->response_code = 200;
    return response;
  }

  HttpWebResponse* GetNetResponse(const char *uri) {
    CURL *curl = curl_easy_init();
    if (!curl) return nullptr;

    HttpWebResponse* response = new HttpWebResponse();

    curl_easy_setopt(curl, CURLOPT_URL, uri);
    curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, callback);
    curl_easy_setopt(curl, CURLOPT_WRITEDATA, is_);

    struct curl_slist *chunk = NULL;
    for (const auto& pair : header_) {
      std::string header_string = pair.first + ": " + pair.second;
      chunk = curl_slist_append(chunk, header_string.c_str());
    }
    curl_easy_setopt(curl, CURLOPT_HTTPHEADER, chunk);


    CURLcode res = curl_easy_perform(curl);
    curl_easy_getinfo(curl, CURLINFO_RESPONSE_CODE, &response->response_code);
    if (res != CURLE_OK) {
      curl_easy_cleanup(curl);
      curl_slist_free_all(chunk);
      delete response;
      return nullptr;
    }
    response->data = is_->str();

    curl_easy_cleanup(curl);
    curl_slist_free_all(chunk);
    return response;
  }

  HttpWebResponse* GetResponse() {
    if (uri_.substr(0, 7) == "file://") {
      return GetFileResponse(uri_.c_str());
    } else {
      return GetNetResponse(uri_.c_str());
    }
  }

  ~HttpWebRequest() {
    delete is_;
  }
};
