#include <iostream>
#include <string>
#include <vector>
#include <queue>
#include <thread>
#include <mutex>
#include <unordered_set>
#include <condition_variable>
#include <curl/curl.h>

class Task {
public:
    std::string m_url;
    std::string m_output;

    Task(const std::string& url, const std::string& output) : m_url(url), m_output(output) {}

    size_t writeData(void* buffer, size_t size, size_t nmemb) {
        FILE* fp = fopen(m_output.c_str(), "wb");
        if (!fp) return 0;
        size_t written = fwrite(buffer, size, nmemb, fp);
        fclose(fp);
        return written;
    }

    void run() {
        CURL* curl = curl_easy_init();
        if (curl) {
            curl_easy_setopt(curl, CURLOPT_URL, m_url.c_str());
            curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, &Task::writeData);
            curl_easy_setopt(curl, CURLOPT_WRITEDATA, this);
            CURLcode res = curl_easy_perform(curl);
            curl_easy_cleanup(curl);
        }
    }
};

class DownloadPool {
public:
    std::vector<std::thread> m_threads;
    std::queue<Task> m_queue;
    std::unordered_set<std::string> m_urls;
    std::mutex m_mtx;
    std::condition_variable m_cv;
    bool m_noMoreTask = false;

    DownloadPool() {
        curl_global_init(CURL_GLOBAL_ALL);
        for (int i = 0; i < 5; ++i) {
            m_threads.emplace_back(&DownloadPool::run, this);
        }
    }

    ~DownloadPool() {
        finishAndWaitForExit();
        curl_global_cleanup();
    }

    void addTask(const std::string& url, const std::string& output) {
        std::lock_guard<std::mutex> lock(m_mtx);
        if (m_urls.find(url) == m_urls.end()) {
            m_queue.emplace(url, output);
            m_urls.insert(url);
            m_cv.notify_one();
        }
    }

    void setNoMoreTask() {
        std::lock_guard<std::mutex> lock(m_mtx);
        m_noMoreTask = true;
        m_cv.notify_all();
    }

    void finishAndWaitForExit() {
        setNoMoreTask();
        for (auto& thread : m_threads) {
            if (thread.joinable()) {
                thread.join();
            }
        }
    }

    void run() {
        while (true) {
            std::unique_lock<std::mutex> lock(m_mtx);
            m_cv.wait(lock, [this] { return !m_queue.empty() || m_noMoreTask; });
            if (m_noMoreTask && m_queue.empty()) {
                return;
            }
            Task task = std::move(m_queue.front());
            m_queue.pop();
            lock.unlock();
            task.run();
        }
    }
};
