#include <iostream>
#include <string>
#include <vector>
#include <stdexcept>
#include <chrono>
#include <sys/socket.h>
#include <netinet/in.h>
#include <arpa/inet.h>
#include <unistd.h>
#include <cstring>

class LinHttpHandler {
public:
    class SocketCloser {
    public:
        SocketCloser(int sockFd) : sockFd(sockFd) {}
        ~SocketCloser() { close(sockFd); }
    private:
        int sockFd;
    };

    std::string send(const std::string& msg, const std::string& adr, int port) {
        int sock = socket(AF_INET, SOCK_STREAM, 0);
        if (sock == -1) throw std::system_error(errno, std::generic_category(), "Failed to create socket");

        SocketCloser closer(sock);

        sockaddr_in serverAddr{};
        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(port);
        if (inet_pton(AF_INET, adr.c_str(), &serverAddr.sin_addr) <= 0) {
            throw std::system_error(errno, std::generic_category(), "Invalid address/ Address not supported");
        }

        if (connect(sock, reinterpret_cast<sockaddr*>(&serverAddr), sizeof(serverAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Connection failed");
        }

        if (send(sock, msg.c_str(), msg.size(), 0) != static_cast<ssize_t>(msg.size())) {
            throw std::system_error(errno, std::generic_category(), "Failed to send data");
        }

        char buffer[4096];
        ssize_t bytesReceived = recv(sock, buffer, sizeof(buffer) - 1, 0);
        if (bytesReceived <= 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to receive data");
        }
        buffer[bytesReceived] = '\0';

        return std::string(buffer);
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sock = socket(AF_INET, SOCK_DGRAM, 0);
        if (sock == -1) throw std::system_error(errno, std::generic_category(), "Failed to create socket");

        SocketCloser closer(sock);

        sockaddr_in multicastAddr{};
        multicastAddr.sin_family = AF_INET;
        multicastAddr.sin_port = htons(port);
        if (inet_pton(AF_INET, adr.c_str(), &multicastAddr.sin_addr) <= 0) {
            throw std::system_error(errno, std::generic_category(), "Invalid address/ Address not supported");
        }

        if (sendto(sock, msg.c_str(), msg.size(), 0, reinterpret_cast<sockaddr*>(&multicastAddr), sizeof(multicastAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to send data");
        }

        std::vector<std::string> responses;
        fd_set readfds;
        FD_ZERO(&readfds);
        FD_SET(sock, &readfds);
        struct timeval tv{};
        tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(timeout).count();
        tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout).count() % 1000000;

        while (select(sock + 1, &readfds, nullptr, nullptr, &tv) > 0) {
            char buffer[4096];
            sockaddr_in clientAddr{};
            socklen_t addrLen = sizeof(clientAddr);
            ssize_t bytesReceived = recvfrom(sock, buffer, sizeof(buffer) - 1, 0, reinterpret_cast<sockaddr*>(&clientAddr), &addrLen);
            if (bytesReceived <= 0) break;
            buffer[bytesReceived] = '\0';
            responses.emplace_back(buffer);
            FD_ZERO(&readfds);
            FD_SET(sock, &readfds);
        }

        return responses;
    }
};

