            struct timeval tv;
            fd_set fdset;
            FD_ZERO(&fdset);
            FD_SET(sockFD, &fdset);

            tv.tv_sec = timeout_seconds;
            tv.tv_usec = 0;
            int select_res = ::select(static_cast<int>(sockFD + 1), NULL, &fdset, NULL, &tv);
#ifdef _WIN32
            if (select_res == 0) {
                return false;
            }
