        tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(timeout).count();
        tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout).count() % 1000000;

        if (select(main_socket + 1, NULL, &fds, NULL, &tv) <= 0) return false;

        ssize_t bytes_sent = send(main_socket, static_cast<const char*>(buffer), size, 0);
        return bytes_sent == static_cast<ssize_t>(size);
