        int so_error;
        socklen_t len = sizeof(so_error);
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, reinterpret_cast<char*>(&so_error), &len) < 0) return false;

        return so_error == 0;
    }
};

