};

class CurlHttpClient {
public:
    static size_t WriteMemoryCallback(void* contents, size_t size, size_t nmemb, void* userp) {
        size_t realsize = size * nmemb;
        MemoryStruct* mem = (MemoryStruct*)userp;

        char* ptr = (char*)realloc(mem->memory, mem->size + realsize + 1);
        if(ptr == NULL) {
            return 0;  // out of memory!
        }

        mem->memory = ptr;
        memcpy(&(mem->memory[mem->size]), contents, realsize);
        mem->size += realsize;
        mem->memory[mem->size] = 0;

        return realsize;
    }

    long downloadPage(const std::string& url, std::string& res, const std::vector<std::string>& headers, const std::string& body) {
        CURL* curl;
        CURLcode resCode;
        MemoryStruct chunk;

        chunk.memory = (char*)malloc(1);  // will be grown as needed by the realloc above
        chunk.size = 0;    // no data at this point

        curl_global_init(CURL_GLOBAL_DEFAULT);

        curl = curl_easy_init();
        if(curl) {
            struct curl_slist* headersList = NULL;
            for(const auto& header : headers) {
                headersList = curl_slist_append(headersList, header.c_str());
            }

            curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
            curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, WriteMemoryCallback);
            curl_easy_setopt(curl, CURLOPT_WRITEDATA, (void*)&chunk);
            curl_easy_setopt(curl, CURLOPT_HTTPHEADER, headersList);
            if(!body.empty()) {
                curl_easy_setopt(curl, CURLOPT_POSTFIELDS, body.c_str());
            }

            resCode = curl_easy_perform(curl);

            if(resCode != CURLE_OK) {
                fprintf(stderr, "curl_easy_perform() failed: %s\n", curl_easy_strerror(resCode));
            } else {
                res.assign(chunk.memory, chunk.size);
            }

            curl_easy_cleanup(curl);
            curl_slist_free_all(headersList);
        }

        free(chunk.memory);
        curl_global_cleanup();

        return resCode;
    }

    long getRequest(const std::string& url, json& res, const std::vector<std::string>& headers, const std::string& body) {
        std::string response;
        long httpCode = downloadPage(url, response, headers, body);

        if(httpCode == 200) {
            try {
