class Stats {
public:
    Stats(const std::string& stats_socket_path) : stats_socket_path_(stats_socket_path), sockfd_(-1), statsThreadRunning_(false), thread_count_(0) {
        memset(&serv_addr_, 0, sizeof(serv_addr_));
    }

    ~Stats() {
        statsThreadRunning_ = false;
        if (stats_thread_.joinable()) {
            stats_thread_.join();
        }
        close(sockfd_);
        unlink(stats_socket_path_.c_str());
    }

    static Stats& get(const std::string& stats_socket_path) {
        std::lock_guard<std::mutex> lock(init_mutex_);
        if (!instance_) {
            instance_ = std::make_unique<Stats>(stats_socket_path);
        }
        return *instance_;
    }

    static std::unique_ptr<Stats> get_for_unittest(const std::string& stats_socket_path) {
        return std::make_unique<Stats>(stats_socket_path);
    }

    static bool init(const std::string& stats_socket_path) {
        std::lock_guard<std::mutex> lock(init_mutex_);
        if (!instance_) {
            instance_ = std::make_unique<Stats>(stats_socket_path);
        }
        return instance_->startSocket();
    }

    bool isInit() {
        std::lock_guard<std::mutex> lock(init_mutex_);
        return instance_ != nullptr;
    }

    std::unordered_map<std::string, int> getAll() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        return stats_;
    }

    int increment(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        return stats_[key] += val;
    }

    int set(const std::string& key, int val) {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        return stats_[key] = val;
    }

    int reset() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_.clear();
        return 0;
    }

private:
    bool startSocket() {
        sockfd_ = socket(AF_UNIX, SOCK_STREAM, 0);
        if (sockfd_ < 0) {
            return false;
        }

        serv_addr_.sun_family = AF_UNIX;
        strncpy(serv_addr_.sun_path, stats_socket_path_.c_str(), sizeof(serv_addr_.sun_path) - 1);

        unlink(stats_socket_path_.c_str());

        if (bind(sockfd_, (struct sockaddr*)&serv_addr_, sizeof(serv_addr_)) < 0) {
            close(sockfd_);
            return false;
        }

        listen(sockfd_, 5);

        statsThreadRunning_ = true;
        stats_thread_ = std::thread(&Stats::runSocket, this);

        return true;
    }

    void runSocket() {
        while (statsThreadRunning_) {
            struct sockaddr_un cli_addr;
            socklen_t clilen = sizeof(cli_addr);
            int newsockfd = accept(sockfd_, (struct sockaddr*)&cli_addr, &clilen);
            if (newsockfd < 0) {
                continue;
            }

            std::lock_guard<std::mutex> lock(thread_mutex_);
            ++thread_count_;

            std::thread clientThread(&Stats::processMsg, this, newsockfd);
            clientThread.detach();
        }
    }

    void processMsg(int sockfd) {
        char buffer[256];
        bzero(buffer, 256);
        read(sockfd, buffer, 255);

        std::istringstream iss(buffer);
        std::string command;
        iss >> command;

        std::string response;
        if (command == "get") {
            std::lock_guard<std::mutex> lock(stats_mutex_);
            for (const auto& pair : stats_) {
                response += pair.first + "=" + std::to_string(pair.second) + " ";
            }
        } else if (command == "reset") {
            reset();
            response = "Reset complete";
        } else {
            response = "Unknown command";
        }

        write(sockfd, response.c_str(), response.size());
        close(sockfd);

        std::lock_guard<std::mutex> lock(thread_mutex_);
        --thread_count_;
        if (thread_count_ == 0) {
            thread_exited_.notify_all();
        }
    }

    static bool& isInitInternal() {
        static bool initialized = false;
        return initialized;
    }

private:
    std::string stats_socket_path_;
    int sockfd_;
    struct sockaddr_un serv_addr_;
    std::thread stats_thread_;
    std::atomic<bool> statsThreadRunning_;
    std::mutex stats_mutex_;
    std::mutex thread_mutex_;
    std::condition_variable thread_exited_;
    int thread_count_;
    std::unordered_map<std::string, int> stats_;

    static std::unique_ptr<Stats> instance_;
    static std::mutex init_mutex_;
};

std::unique_ptr<Stats> Stats::instance_ = nullptr;
std::mutex Stats::init_mutex_;

