class UDPRecv {
private:
    DFNetWork* m_poDFNetWork;
    int m_iSockFD;
    bool m_bIsEnd;
    bool m_bIsStarted;

public:
    UDPRecv(DFNetWork* poDFNetWork) : m_poDFNetWork(poDFNetWork), m_iSockFD(-1), m_bIsEnd(false), m_bIsStarted(false) {}

    ~UDPRecv() {
        Stop();
        if (m_iSockFD != -1) {
            close(m_iSockFD);
        }
    }

    void Stop() {
        m_bIsEnd = true;
        if (m_bIsStarted) {
            joinThread.join();
        }
    }

    int Init(int iPort) {
        m_iSockFD = socket(AF_INET, SOCK_DGRAM, 0);
        if (m_iSockFD == -1) {
            return -1;
        }

        sockaddr_in addr;
        addr.sin_family = AF_INET;
        addr.sin_port = htons(iPort);
        addr.sin_addr.s_addr = INADDR_ANY;

        if (bind(m_iSockFD, (sockaddr*)&addr, sizeof(addr)) == -1) {
            close(m_iSockFD);
            m_iSockFD = -1;
            return -1;
        }

        return 0;
    }

    void run() {
        m_bIsStarted = true;
        char buffer[1024];
        sockaddr_in clientAddr;
        socklen_t addrLen = sizeof(clientAddr);

        while (!m_bIsEnd) {
            pollfd fds[1];
            fds[0].fd = m_iSockFD;
            fds[0].events = POLLIN;

            int ret = poll(fds, 1, 100); // Timeout set to 100ms
            if (ret > 0 && (fds[0].revents & POLLIN)) {
                ssize_t bytesRead = recvfrom(m_iSockFD, buffer, sizeof(buffer), 0, (sockaddr*)&clientAddr, &addrLen);
                if (bytesRead > 0) {
                    m_poDFNetWork->ProcessMessage(buffer, bytesRead);
                }
            }
        }
        m_bIsStarted = false;
    }

private:
    std::thread joinThread;
};

