class DownloadTask {
public:
    static void initialize() {
        curl_global_init(CURL_GLOBAL_DEFAULT);
    }

    static void uninitialize() {
        curl_global_cleanup();
    }

    DownloadTask(const std::string& url, const std::string& output, const std::string& def, time_t mtime, const std::string& name = "")
        : m_url(url), m_output(output), m_default(def), m_mtime(mtime), m_name(name), m_retries(0) {
        m_outputTmp = m_output + ".tmp";
        m_userAgent = "libcurl-agent/1.0";
    }

    bool run() {
        bool success = false;
        while (!success && m_retries < 3) {
            success = downloadFile(m_url);
            if (!success && !m_urlBackup.empty()) {
                success = downloadFile(m_urlBackup);
            }
            m_retries++;
        }
        if (success) {
            std::rename(m_outputTmp.c_str(), m_output.c_str());
            utimbuf new_times = {m_mtime, m_mtime};
            utime(m_output.c_str(), &new_times);
        } else {
            std::rename(m_default.c_str(), m_output.c_str());
        }
        return success;
    }

    unsigned int getRetries() const {
        return m_retries;
    }

private:
    std::string m_url;
    std::string m_urlBackup;
    std::string m_output;
    std::string m_outputTmp;
    std::string m_default;
    time_t m_mtime;
    unsigned int m_retries;
    std::string m_name;
    std::string m_userAgent;
    std::string m_error;

    static size_t writeData(void* ptr, size_t size, size_t nmemb, FILE* stream) {
        return fwrite(ptr, size, nmemb, stream);
    }

    bool downloadFile(const std::string& url) {
        CURL* curl = curl_easy_init();
        if (!curl) {
            m_error = "Failed to create CURL session.";
            return false;
        }
        FILE* tmpFile = fopen(m_outputTmp.c_str(), "wb");
        if (!tmpFile) {
            m_error = "Failed to open temporary file.";
            curl_easy_cleanup(curl);
            return false;
        }
        curl_easy_setopt(curl, CURLOPT_URL, url.c_str());
        curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, writeData);
        curl_easy_setopt(curl, CURLOPT_WRITEDATA, tmpFile);
        curl_easy_setopt(curl, CURLOPT_USERAGENT, m_userAgent.c_str());
        CURLcode res = curl_easy_perform(curl);
        fclose(tmpFile);
        curl_easy_cleanup(curl);
        if (res != CURLE_OK) {
            m_error = curl_easy_strerror(res);
            remove(m_outputTmp.c_str());
            return false;
        }
        return true;
    }
};

