        assert(strlen(uri) > 6);
        HttpWebResponse *resp = new HttpWebResponse();
        const char *filename = uri + 6;

        std::ifstream file;
        file.open(filename);
        if (file.is_open()) {
            resp->os_ << file.rdbuf();
            file.close();
        }

        return resp;
    }

