public:
    HttpWebRequest(std::string &uri) : uri_(uri), method_("GET") {}

    static size_t callback(void *buffer, size_t size, size_t nmemb, void *data) {
        std::string *str = static_cast<std::string*>(data);
        str->append((char*)buffer, size * nmemb);
        return size * nmemb;
    }

    HttpWebResponse* GetFileResponse(const char *uri) {
        HttpWebResponse *response = new HttpWebResponse();
        std::ifstream file(uri + 7); // Skip "file://"
        if (file.is_open()) {
            response->content.assign((std::istreambuf_iterator<char>(file)), std::istreambuf_iterator<char>());
            response->statusCode = 200;
            file.close();
        } else {
            response->statusCode = 404;
        }
        return response;
    }

    HttpWebResponse* GetNetResponse(const char *uri) {
        CURL *curl;
        CURLcode res;
        std::string readBuffer;

        curl_global_init(CURL_GLOBAL_DEFAULT);
        curl = curl_easy_init();
        if(curl) {
            curl_easy_setopt(curl, CURLOPT_URL, uri);
            curl_easy_setopt(curl, CURLOPT_WRITEFUNCTION, callback);
            curl_easy_setopt(curl, CURLOPT_WRITEDATA, &readBuffer);
