            curl_easy_setopt(curl, CURLOPT_WRITEDATA, fp);
            res = curl_easy_perform(curl);
            curl_easy_cleanup(curl);
            fclose(fp);
        }
    }

    static size_t writeData(void* buffer, size_t size, size_t nmemb, void* userp) {
        size_t written = fwrite(buffer, size, nmemb, (FILE*)userp);
        return written;
    }

private:
    std::string m_url;
    std::string m_output;
};

class DownloadPool {
public:
    DownloadPool(size_t numThreads) : m_noMoreTask(false) {
        curl_global_init(CURL_GLOBAL_DEFAULT);
        for (size_t i = 0; i < numThreads; ++i) {
            m_threads.emplace_back(&DownloadPool::run, this);
        }
    }

    ~DownloadPool() {
        finishAndWaitForExit();
        curl_global_cleanup();
    }

    void addTask(const std::string& url, const std::string& output) {
        std::lock_guard<std::mutex> lock(m_mtx);
        if (m_urls.find(url) == m_urls.end()) {
            m_queue.push({url, output});
            m_urls.insert(url);
            m_cv.notify_one();
        }
    }

    void setNoMoreTask() {
        std::lock_guard<std::mutex> lock(m_mtx);
        m_noMoreTask = true;
        m_cv.notify_all();
    }

    void finishAndWaitForExit() {
        {
            std::lock_guard<std::mutex> lock(m_mtx);
            m_noMoreTask = true;
        }
        m_cv.notify_all();
        for (auto& t : m_threads) {
            if (t.joinable()) {
                t.join();
            }
        }
    }

private:
    void run() {
        while (true) {
            Task task;
            {
                std::unique_lock<std::mutex> lock(m_mtx);
                m_cv.wait(lock, [this] { return !m_queue.empty() || m_noMoreTask; });
                if (m_noMoreTask && m_queue.empty()) {
                    break;
                }
                task = m_queue.front();
                m_queue.pop();
            }
            task.run();
        }
