                }
            });
    }

    void do_read_body() {
        boost::asio::async_read(socket_,
            boost::asio::buffer(read_msg_.body(), read_msg_.body_length()),
            [this](boost::system::error_code ec, std::size_t /*length*/) {
                if (!ec) {
                    do_write_output();
                    do_read_header();
                } else {
                    close();
                }
            });
    }

    void do_write_output() {
        boost::asio::async_write(output_,
            boost::asio::buffer(read_msg_.body(), read_msg_.body_length()),
            [this](boost::system::error_code ec, std::size_t /*length*/) {
                if (ec) {
                    close();
                }
            });
    }

    void do_read_input() {
        boost::asio::async_read_until(input_, input_buffer_, '\n',
            [this](boost::system::error_code ec, std::size_t length) {
                if (!ec) {
                    write_msg_.body_length(length - 1);
                    boost::asio::buffer_copy(boost::asio::buffer(write_msg_.body(), write_msg_.body_length()),
                        input_buffer_.data());
                    input_buffer_.consume(length);
                    write_msg_.encode_header();
                    do_write_message();
                } else {
                    close();
                }
            });
    }

    void do_write_message() {
        boost::asio::async_write(socket_,
            boost::asio::buffer(write_msg_.data(), write_msg_.length()),
            [this](boost::system::error_code ec, std::size_t /*length*/) {
                if (!ec) {
                    do_read_input();
                } else {
                    close();
                }
            });
    }

    void close() {
        boost::system::error_code ignored_ec;
        socket_.shutdown(tcp::socket::shutdown_both, ignored_ec);
        socket_.close(ignored_ec);
        input_.close(ignored_ec);
        output_.close(ignored_ec);
    }

private:
    tcp::socket socket_;
    boost::asio::posix::stream_descriptor input_;
    boost::asio::posix::stream_descriptor output_;
    chat_message read_msg_;
    chat_message write_msg_;
    boost::asio::streambuf input_buffer_;
};

