    };

    std::string send(const std::string& msg, const std::string& adr, int port) {
        int sock = socket(AF_INET, SOCK_STREAM, 0);
        if (sock < 0) throw std::system_error(errno, std::generic_category(), "Failed to create socket");

        SocketCloser closer(sock);

        sockaddr_in servAddr{};
        servAddr.sin_family = AF_INET;
        servAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &servAddr.sin_addr);

        if (connect(sock, reinterpret_cast<sockaddr*>(&servAddr), sizeof(servAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to connect");
        }

        if (send(sock, msg.c_str(), msg.size(), 0) != static_cast<ssize_t>(msg.size())) {
            throw std::system_error(errno, std::generic_category(), "Failed to send data");
        }

        char buffer[4096];
        ssize_t bytesReceived = recv(sock, buffer, sizeof(buffer) - 1, 0);
        if (bytesReceived < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to receive data");
        }
        buffer[bytesReceived] = '\0';

        return std::string(buffer);
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sock = socket(AF_INET, SOCK_DGRAM, 0);
        if (sock < 0) throw std::system_error(errno, std::generic_category(), "Failed to create socket");

        SocketCloser closer(sock);

        sockaddr_in servAddr{};
        servAddr.sin_family = AF_INET;
        servAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &servAddr.sin_addr);

        if (sendto(sock, msg.c_str(), msg.size(), 0, reinterpret_cast<sockaddr*>(&servAddr), sizeof(servAddr)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Failed to send multicast data");
        }

        fd_set readfds;
        FD_ZERO(&readfds);
        FD_SET(sock, &readfds);

        auto end = std::chrono::steady_clock::now() + timeout;
        while (std::chrono::steady_clock::now() < end) {
            struct timeval tv;
            auto remaining = std::chrono::duration_cast<std::chrono::microseconds>(end - std::chrono::steady_clock::now());
            tv.tv_sec = remaining.count() / 1000000;
            tv.tv_usec = remaining.count() % 1000000;

            int activity = select(sock + 1, &readfds, nullptr, nullptr, &tv);
            if (activity < 0) {
                throw std::system_error(errno, std::generic_category(), "Select failed");
            } else if (activity == 0) {
                break; // Timeout
            }

            if (FD_ISSET(sock, &readfds)) {
                char buffer[4096];
                sockaddr_in clientAddr{};
                socklen_t addrLen = sizeof(clientAddr);
                ssize_t bytesReceived = recvfrom(sock, buffer, sizeof(buffer) - 1, 0, reinterpret_cast<sockaddr*>(&clientAddr), &addrLen);
                if (bytesReceived < 0) {
                    throw std::system_error(errno, std::generic_category(), "Failed to receive multicast data");
                }
                buffer[bytesReceived] = '\0';
                responses.push_back(std::string(buffer));
            }
        }

        return responses;
    }

private:
    std::vector<std::string> responses;
};

