            auto remaining = end - std::chrono::steady_clock::now();
            struct timeval tv{};
            tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(remaining).count();
            tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(remaining % std::chrono::seconds(1)).count();

            int activity = select(sock + 1, &fds, nullptr, nullptr, &tv);
            if (activity < 0 && errno != EINTR) {
                throw std::system_error(errno, std::generic_category(), "Select failed");
            } else if (activity > 0) {
                char buffer[4096];
                ssize_t valread = recvfrom(sock, buffer, 4095, 0, nullptr, nullptr);
                if (valread < 0) {
                    throw std::system_error(errno, std::generic_category(), "Recvfrom failed");
                }
                buffer[valread] = '\0';
                responses.push_back(std::string(buffer));
            }
        }

        if (setsockopt(sock, IPPROTO_IP, IP_DROP_MEMBERSHIP, &mreq, sizeof(mreq)) < 0) {
            throw std::system_error(errno, std::generic_category(), "Setsockopt failed");
        }

        return responses;
    }

private:
    std::vector<std::string> responses;
};

