#include <chrono>
#include <stdexcept>
#include <sys/socket.h>
#include <netinet/in.h>
#include <arpa/inet.h>
#include <unistd.h>
#include <cstring>
#include <fcntl.h>

class LinHttpHandler {
public:
    class SocketCloser {
    public:
        SocketCloser(int sockFd) : sockFd(sockFd) {}
        ~SocketCloser() { close(sockFd); }
    private:
        int sockFd;
    };

    std::string send(const std::string& msg, const std::string& adr, int port) {
        int sock = socket(AF_INET, SOCK_STREAM, 0);
        if (sock == -1) throw std::system_error(errno, std::system_category(), "Failed to create socket");

        SocketCloser closer(sock);

        sockaddr_in serverAddr{};
        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(port);
        if (inet_pton(AF_INET, adr.c_str(), &serverAddr.sin_addr) <= 0) {
            throw std::system_error(errno, std::system_category(), "Invalid address/ Address not supported");
        }

        if (connect(sock, (struct sockaddr*)&serverAddr, sizeof(serverAddr)) < 0) {
            throw std::system_error(errno, std::system_category(), "Connection failed");
        }

        if (send(sock, msg.c_str(), msg.size(), 0) < 0) {
            throw std::system_error(errno, std::system_category(), "Send failed");
        }

        char buffer[4096];
        ssize_t valread = read(sock, buffer, 4095);
        if (valread <= 0) {
            throw std::system_error(errno, std::system_category(), "Read failed");
        }
        buffer[valread] = '\0';

        return std::string(buffer);
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sock = socket(AF_INET, SOCK_DGRAM, 0);
        if (sock == -1) throw std::system_error(errno, std::system_category(), "Failed to create socket");

        SocketCloser closer(sock);

        int optval = 1;
        if (setsockopt(sock, SOL_SOCKET, SO_REUSEADDR, &optval, sizeof(optval)) < 0) {
            throw std::system_error(errno, std::system_category(), "Setsockopt failed");
        }

        sockaddr_in localAddr{};
        localAddr.sin_family = AF_INET;
        localAddr.sin_port = htons(port);
        localAddr.sin_addr.s_addr = htonl(INADDR_ANY);

        if (bind(sock, (struct sockaddr*)&localAddr, sizeof(localAddr)) < 0) {
            throw std::system_error(errno, std::system_category(), "Bind failed");
        }

        struct ip_mreq mreq{};
        mreq.imr_multiaddr.s_addr = inet_addr(adr.c_str());
        mreq.imr_interface.s_addr = htonl(INADDR_ANY);

        if (setsockopt(sock, IPPROTO_IP, IP_ADD_MEMBERSHIP, &mreq, sizeof(mreq)) < 0) {
            throw std::system_error(errno, std::system_category(), "Setsockopt failed");
        }

        sockaddr_in serverAddr{};
        serverAddr.sin_family = AF_INET;
        serverAddr.sin_port = htons(port);
        serverAddr.sin_addr.s_addr = inet_addr(adr.c_str());

        if (sendto(sock, msg.c_str(), msg.size(), 0, (struct sockaddr*)&serverAddr, sizeof(serverAddr)) < 0) {
            throw std::system_error(errno, std::system_category(), "Sendto failed");
        }

        fd_set fds;
        FD_ZERO(&fds);
        FD_SET(sock, &fds);

        auto end = std::chrono::steady_clock::now() + timeout;
        while (true) {
            auto now = std::chrono::steady_clock::now();
            if (now >= end) break;

            struct timeval tv{};
            auto remaining = std::chrono::duration_cast<std::chrono::microseconds>(end - now);
            tv.tv_sec = remaining.count() / 1000000;
            tv.tv_usec = remaining.count() % 1000000;

            int ret = select(sock + 1, &fds, nullptr, nullptr, &tv);
            if (ret == -1) throw std::system_error(errno, std::system_category(), "Select failed");
            if (ret == 0) break;

            char buffer[4096];
            ssize_t valread = recvfrom(sock, buffer, 4095, 0, nullptr, nullptr);
            if (valread <= 0) continue;
            buffer[valread] = '\0';
            responses.push_back(std::string(buffer));
        }

        if (setsockopt(sock, IPPROTO_IP, IP_DROP_MEMBERSHIP, &mreq, sizeof(mreq)) < 0) {
            throw std::system_error(errno, std::system_category(), "Setsockopt failed");
        }

        return responses;
    }

private:
    std::vector<std::string> responses;
};

