#include <thread>
#include <mutex>
#include <condition_variable>

class LinHttpHandler {
public:
    class SocketCloser {
    public:
        explicit SocketCloser(int sockFd) : sockFd(sockFd) {}
        ~SocketCloser() { close(sockFd); }
    private:
        int sockFd;
    };

    std::string send(const std::string& msg, const std::string& adr, int port) {
        int sock = socket(AF_INET, SOCK_STREAM, 0);
        if (sock == -1) throw std::system_error(errno, std::generic_category(), "Failed to create socket");

        SocketCloser closer(sock);

        sockaddr_in servAddr{};
        servAddr.sin_family = AF_INET;
        servAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &servAddr.sin_addr);

        if (connect(sock, reinterpret_cast<sockaddr*>(&servAddr), sizeof(servAddr)) == -1)
            throw std::system_error(errno, std::generic_category(), "Failed to connect");

        if (send(sock, msg.c_str(), msg.size(), 0) == -1)
            throw std::system_error(errno, std::generic_category(), "Failed to send data");

        char buffer[4096];
        ssize_t bytesRead = recv(sock, buffer, sizeof(buffer) - 1, 0);
        if (bytesRead == -1)
            throw std::system_error(errno, std::generic_category(), "Failed to receive data");

        buffer[bytesRead] = '\0';
        return std::string(buffer);
    }

    std::vector<std::string> sendMulticast(const std::string& msg, const std::string& adr, int port, std::chrono::steady_clock::duration timeout) {
        int sock = socket(AF_INET, SOCK_DGRAM, 0);
        if (sock == -1) throw std::system_error(errno, std::generic_category(), "Failed to create socket");

        SocketCloser closer(sock);

        sockaddr_in servAddr{};
        servAddr.sin_family = AF_INET;
        servAddr.sin_port = htons(port);
        inet_pton(AF_INET, adr.c_str(), &servAddr.sin_addr);

        if (sendto(sock, msg.c_str(), msg.size(), 0, reinterpret_cast<sockaddr*>(&servAddr), sizeof(servAddr)) == -1)
            throw std::system_error(errno, std::generic_category(), "Failed to send multicast data");

        std::vector<std::string> responses;
        std::mutex mtx;
        std::condition_variable cv;
        bool done = false;

        auto listener = [&]() {
            char buffer[4096];
            while (true) {
                sockaddr_in clientAddr{};
                socklen_t addrLen = sizeof(clientAddr);
                ssize_t bytesRead = recvfrom(sock, buffer, sizeof(buffer) - 1, 0, reinterpret_cast<sockaddr*>(&clientAddr), &addrLen);
                if (bytesRead == -1) break;

                buffer[bytesRead] = '\0';
                std::lock_guard<std::mutex> lock(mtx);
                responses.push_back(std::string(buffer));
            }
            std::lock_guard<std::mutex> lock(mtx);
            done = true;
            cv.notify_one();
        };

        std::thread t(listener);

        {
            std::unique_lock<std::mutex> lock(mtx);
            cv.wait_for(lock, timeout, [&]{ return done; });
        }

        t.join();

        return responses;
    }
};

