using namespace std;

typedef int SocketHolder;

class TCPClient {
public:
    string address;
    uint16_t port;
    SocketHolder main_socket;
    bool connected;

    TCPClient(string addr, uint16_t p) : address(addr), port(p), main_socket(-1), connected(false) {}

    ~TCPClient() {
        Disconnect();
    }

    bool Connect() {
#ifdef _WIN32
        WSADATA wsaData;
        if (WSAStartup(MAKEWORD(2, 2), &wsaData) != 0) {
            return false;
        }
#endif

        main_socket = socket(AF_INET, SOCK_STREAM, 0);
        if (main_socket == -1) {
            return false;
        }

        struct sockaddr_in server_addr;
        memset(&server_addr, 0, sizeof(server_addr));
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(port);

        if (inet_pton(AF_INET, address.c_str(), &server_addr.sin_addr) <= 0) {
            Disconnect();
            return false;
        }

        if (!ConnectWithTimeout(main_socket, &server_addr, sizeof(server_addr), 5)) {
            Disconnect();
            return false;
        }

        connected = true;
        return true;
    }

    bool Read(void* buffer, size_t size, const chrono::nanoseconds& timeout) {
        if (!SetNonBlock(main_socket, true)) {
            return false;
        }

        auto start = chrono::steady_clock::now();
        size_t total_read = 0;

        while (total_read < size) {
            fd_set read_fds;
            FD_ZERO(&read_fds);
            FD_SET(main_socket, &read_fds);

            struct timeval tv;
            tv.tv_sec = chrono::duration_cast<chrono::seconds>(timeout).count();
            tv.tv_usec = (timeout % chrono::seconds(1)).count() * 1000;

            int select_result = select(main_socket + 1, &read_fds, nullptr, nullptr, &tv);
            if (select_result == -1) {
                return false;
            } else if (select_result == 0) {
                return false; // Timeout
            }

            ssize_t bytes_read = recv(main_socket, static_cast<char*>(buffer) + total_read, size - total_read, 0);
            if (bytes_read <= 0) {
                return false;
            }

            total_read += bytes_read;

            if (chrono::steady_clock::now() - start > timeout) {
                return false; // Timeout
            }
        }

        return true;
    }

    bool Write(const void* buffer, size_t size, const chrono::nanoseconds& timeout) {
        if (!SetNonBlock(main_socket, true)) {
            return false;
        }

        auto start = chrono::steady_clock::now();
        size_t total_written = 0;

        while (total_written < size) {
            fd_set write_fds;
            FD_ZERO(&write_fds);
            FD_SET(main_socket, &write_fds);

            struct timeval tv;
            tv.tv_sec = chrono::duration_cast<chrono::seconds>(timeout).count();
            tv.tv_usec = (timeout % chrono::seconds(1)).count() * 1000;

            int select_result = select(main_socket + 1, nullptr, &write_fds, nullptr, &tv);
            if (select_result == -1) {
                return false;
            } else if (select_result == 0) {
                return false; // Timeout
            }

            ssize_t bytes_written = send(main_socket, static_cast<const char*>(buffer) + total_written, size - total_written, 0);
            if (bytes_written <= 0) {
                return false;
            }

            total_written += bytes_written;

            if (chrono::steady_clock::now() - start > timeout) {
                return false; // Timeout
            }
        }

        return true;
    }

    void Disconnect() {
        if (main_socket != -1) {
#ifdef _WIN32
            closesocket(main_socket);
#else
            close(main_socket);
#endif
            main_socket = -1;
            connected = false;
        }
#ifdef _WIN32
        WSACleanup();
#endif
    }

private:
    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
#ifdef _WIN32
        unsigned long mode = nonBlocking ? 1 : 0;
        return ioctlsocket(sockFD, FIONBIO, &mode) == 0;
#else
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) {
            return false;
        }
        flags = nonBlocking ? (flags | O_NONBLOCK) : (flags & ~O_NONBLOCK);
        return fcntl(sockFD, F_SETFL, flags) == 0;
#endif
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        if (!SetNonBlock(sockFD, true)) {
            return false;
        }

        int result = connect(sockFD, static_cast<struct sockaddr*>(server_addr), server_addr_len);
        if (result == 0) {
            return true;
        }

        if (errno != EINPROGRESS) {
            return false;
        }

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(sockFD, &write_fds);

        struct timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        int select_result = select(sockFD + 1, nullptr, &write_fds, nullptr, &tv);
        if (select_result == -1) {
            return false;
        } else if (select_result == 0) {
            return false; // Timeout
        }

        int so_error;
        socklen_t len = sizeof(so_error);
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, reinterpret_cast<char*>(&so_error), &len) < 0) {
            return false;
        }

        if (so_error != 0) {
            errno = so_error;
            return false;
        }

        if (!SetNonBlock(sockFD, false)) {
            return false;
        }

        return true;
    }
};

