    uint16_t port;
    SocketHolder main_socket;
    bool connected;

    TCPClient(string addr, uint16_t p) : address(addr), port(p), main_socket(-1), connected(false) {}

    bool Connect() {
        #ifdef _WIN32
        WSADATA wsaData;
        if (WSAStartup(MAKEWORD(2, 2), &wsaData) != 0) return false;
        #endif

        main_socket = socket(AF_INET, SOCK_STREAM, 0);
        if (main_socket == -1) return false;

        sockaddr_in server_addr;
        memset(&server_addr, 0, sizeof(server_addr));
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(port);

        if (inet_pton(AF_INET, address.c_str(), &server_addr.sin_addr) <= 0) {
            Disconnect();
            return false;
        }

        if (!ConnectWithTimeout(main_socket, &server_addr, sizeof(server_addr), 5)) {
            Disconnect();
            return false;
        }

        connected = true;
        return true;
    }

    bool Read(void* buffer, size_t size, const chrono::nanoseconds& timeout) {
        if (!connected) return false;
        fd_set read_fds;
        FD_ZERO(&read_fds);
        FD_SET(main_socket, &read_fds);

        struct timeval tv;
        tv.tv_sec = chrono::duration_cast<chrono::seconds>(timeout).count();
        tv.tv_usec = chrono::duration_cast<chrono::microseconds>(timeout % chrono::seconds(1)).count();

        int ret = select(main_socket + 1, &read_fds, nullptr, nullptr, &tv);
        if (ret <= 0) return false;

        ssize_t total_received = 0;
        while (total_received < size) {
            ssize_t received = recv(main_socket, static_cast<char*>(buffer) + total_received, size - total_received, 0);
            if (received <= 0) return false;
            total_received += received;
        }
        return true;
    }

    bool Write(const void* buffer, size_t size, const chrono::nanoseconds& timeout) {
        if (!connected) return false;
        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(main_socket, &write_fds);

        struct timeval tv;
        tv.tv_sec = chrono::duration_cast<chrono::seconds>(timeout).count();
        tv.tv_usec = chrono::duration_cast<chrono::microseconds>(timeout % chrono::seconds(1)).count();

        int ret = select(main_socket + 1, nullptr, &write_fds, nullptr, &tv);
        if (ret <= 0) return false;

        ssize_t total_sent = 0;
        while (total_sent < size) {
            ssize_t sent = send(main_socket, static_cast<const char*>(buffer) + total_sent, size - total_sent, 0);
            if (sent <= 0) return false;
            total_sent += sent;
        }
        return true;
    }

    void Disconnect() {
        if (main_socket != -1) {
            #ifdef _WIN32
            closesocket(main_socket);
            WSACleanup();
            #else
            close(main_socket);
            #endif
            main_socket = -1;
            connected = false;
        }
    }

    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
        #ifdef _WIN32
        unsigned long mode = nonBlocking ? 1 : 0;
        return ioctlsocket(sockFD, FIONBIO, &mode) == 0;
        #else
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) return false;
        flags = nonBlocking ? (flags | O_NONBLOCK) : (flags & ~O_NONBLOCK);
        return fcntl(sockFD, F_SETFL, flags) == 0;
        #endif
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        if (!SetNonBlock(sockFD, true)) return false;

        int ret = connect(sockFD, static_cast<sockaddr*>(server_addr), server_addr_len);
        if (ret == 0) {
            SetNonBlock(sockFD, false);
            return true;
        }

        if (errno != EINPROGRESS) return false;

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(sockFD, &write_fds);

        struct timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        ret = select(sockFD + 1, nullptr, &write_fds, nullptr, &tv);
        if (ret <= 0) return false;

        int so_error;
        socklen_t len = sizeof(so_error);
        getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &so_error, &len);
        if (so_error != 0) return false;

        SetNonBlock(sockFD, false);
        return true;
    }
};

