class TCPClient {
public:
    using SocketHolder = int;
    static constexpr int INVALID_SOCKET_FD = -1;

    TCPClient(const std::string& address, uint16_t port)
        : address(address), port(port), main_socket(INVALID_SOCKET_FD), connected(false) {}

    ~TCPClient() {
        Disconnect();
    }

    bool Connect() {
#ifdef _WIN32
        WSADATA wsaData;
        if (WSAStartup(MAKEWORD(2, 2), &wsaData) != 0) {
            return false;
        }
#endif

        main_socket = socket(AF_INET, SOCK_STREAM, 0);
        if (main_socket == INVALID_SOCKET_FD) {
            return false;
        }

        sockaddr_in server_addr{};
        server_addr.sin_family = AF_INET;
        server_addr.sin_port = htons(port);

        if (inet_pton(AF_INET, address.c_str(), &server_addr.sin_addr) <= 0) {
            Disconnect();
            return false;
        }

        if (!ConnectWithTimeout(main_socket, &server_addr, sizeof(server_addr), 5)) {
            Disconnect();
            return false;
        }

        connected = true;
        return true;
    }

    bool Read(void* buffer, size_t size, const std::chrono::nanoseconds& timeout) {
        if (!SetNonBlock(main_socket, true)) {
            return false;
        }

        auto start = std::chrono::steady_clock::now();
        size_t total_read = 0;

        while (total_read < size) {
            auto elapsed = std::chrono::duration_cast<std::chrono::nanoseconds>(std::chrono::steady_clock::now() - start);
            if (elapsed >= timeout) {
                return false;
            }

            fd_set read_fds;
            FD_ZERO(&read_fds);
            FD_SET(main_socket, &read_fds);

            struct timeval tv{};
            tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(timeout - elapsed).count();
            tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout - elapsed).count() % 1000000;

            int select_result = select(main_socket + 1, &read_fds, nullptr, nullptr, &tv);
            if (select_result == -1) {
                return false;
            } else if (select_result == 0) {
                continue; // Timeout occurred
            }

            ssize_t bytes_read = recv(main_socket, static_cast<char*>(buffer) + total_read, size - total_read, 0);
            if (bytes_read <= 0) {
                return false;
            }

            total_read += bytes_read;
        }

