            if (select_result == -1) {
                return false;
            } else if (select_result == 0) {
                continue; // Timeout occurred
            }

            ssize_t bytes_written = send(main_socket, static_cast<const char*>(buffer) + total_written, size - total_written, 0);
            if (bytes_written <= 0) {
                return false;
            }

            total_written += bytes_written;
        }

        return true;
    }

    void Disconnect() {
        if (main_socket != INVALID_SOCKET_FD) {
#ifdef _WIN32
            closesocket(main_socket);
#else
            close(main_socket);
#endif
            main_socket = INVALID_SOCKET_FD;
            connected = false;
        }

#ifdef _WIN32
        WSACleanup();
#endif
    }

private:
