    uint16_t port;
    SocketHolder main_socket;
    bool connected;

    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
#ifdef _WIN32
        u_long mode = nonBlocking ? 1 : 0;
        return ioctlsocket(sockFD, FIONBIO, &mode) == 0;
#else
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) {
            return false;
        }

        flags = nonBlocking ? (flags | O_NONBLOCK) : (flags & ~O_NONBLOCK);
        return fcntl(sockFD, F_SETFL, flags) == 0;
#endif
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        if (!SetNonBlock(sockFD, true)) {
            return false;
        }

        int result = connect(sockFD, static_cast<sockaddr*>(server_addr), server_addr_len);
        if (result == 0) {
            return true;
        }

        if (errno != EINPROGRESS) {
            return false;
        }

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(sockFD, &write_fds);

        struct timeval tv{};
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        int select_result = select(sockFD + 1, nullptr, &write_fds, nullptr, &tv);
        if (select_result <= 0) {
            return false;
        }

        int so_error;
        socklen_t len = sizeof(so_error);
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, &so_error, &len) < 0) {
            return false;
        }

        return so_error == 0;
    }
};

