                return false;
            }

            fd_set write_fds;
            FD_ZERO(&write_fds);
            FD_SET(main_socket, &write_fds);

            struct timeval tv;
            tv.tv_sec = std::chrono::duration_cast<std::chrono::seconds>(timeout - elapsed).count();
            tv.tv_usec = std::chrono::duration_cast<std::chrono::microseconds>(timeout - elapsed).count() % 1000000;

            int select_result = select(main_socket + 1, nullptr, &write_fds, nullptr, &tv);
            if (select_result == -1) {
                return false;
            } else if (select_result == 0) {
                continue; // Timeout occurred
            }

            ssize_t bytes_written = send(main_socket, static_cast<const char*>(buffer) + total_written, size - total_written, 0);
            if (bytes_written <= 0) {
                return false;
            }

            total_written += bytes_written;
        }

        return true;
    }

    void Disconnect() {
        if (connected) {
#ifdef _WIN32
            closesocket(main_socket);
#else
            close(main_socket);
#endif
            connected = false;
        }
    }

private:
    std::string address;
    u16 port;
    SocketHolder main_socket;
    bool connected;

    bool SetNonBlock(SocketHolder sockFD, bool nonBlocking) {
#ifdef _WIN32
        u_long mode = nonBlocking ? 1 : 0;
        return ioctlsocket(sockFD, FIONBIO, &mode) == 0;
#else
        int flags = fcntl(sockFD, F_GETFL, 0);
        if (flags == -1) {
            return false;
        }
        flags = nonBlocking ? (flags | O_NONBLOCK) : (flags & ~O_NONBLOCK);
        return fcntl(sockFD, F_SETFL, flags) == 0;
#endif
    }

    bool ConnectWithTimeout(SocketHolder sockFD, void* server_addr, size_t server_addr_len, int timeout_seconds) {
        if (!SetNonBlock(sockFD, true)) {
            return false;
        }

        int result = connect(sockFD, static_cast<sockaddr*>(server_addr), server_addr_len);
        if (result == 0) {
            return true;
        }

#ifdef _WIN32
        if (WSAGetLastError() != WSAEWOULDBLOCK) {
            return false;
        }
#else
        if (errno != EINPROGRESS) {
            return false;
        }
#endif

        fd_set write_fds;
        FD_ZERO(&write_fds);
        FD_SET(sockFD, &write_fds);

        struct timeval tv;
        tv.tv_sec = timeout_seconds;
        tv.tv_usec = 0;

        int select_result = select(sockFD + 1, nullptr, &write_fds, nullptr, &tv);
        if (select_result <= 0) {
            return false;
        }

        int so_error;
        socklen_t len = sizeof(so_error);
        if (getsockopt(sockFD, SOL_SOCKET, SO_ERROR, reinterpret_cast<char*>(&so_error), &len) < 0) {
            return false;
        }

        if (so_error != 0) {
            return false;
        }

        return true;
    }
};

