            if (bind(server_sock[i], (sockaddr*)&server_addr, sizeof(server_addr)) < 0) {
                std::cerr << "Error binding socket" << std::endl;
                close(server_sock[i]);
                continue;
            }

            if (listen(server_sock[i], SOMAXCONN) < 0) {
                std::cerr << "Error listening on socket" << std::endl;
                close(server_sock[i]);
                continue;
            }

            ConnectionThread[i] = new std::thread(&NetworkServer::ConnectionThreadFunction, this, i);
            ++socket_count;
        }
    }

    void StopServer() {
        server_online = false;
        server_listening = false;

        for (int i = 0; i < socket_count; ++i) {
            if (ConnectionThread[i]) {
                ConnectionThread[i]->join();
                delete ConnectionThread[i];
                ConnectionThread[i] = nullptr;
            }
            close(server_sock[i]);
        }

        std::lock_guard<std::mutex> lock(ServerClientsMutex);
        for (auto& client : ServerClients) {
            close(client->socket);
            delete client;
        }
        ServerClients.clear();
    }

    void ConnectionThreadFunction(int socket_idx) {
        while (server_online) {
            int client_sock = accept_select(server_sock[socket_idx]);
            if (client_sock < 0) {
                if (!server_online) break;
                continue;
            }

            sockaddr_in client_addr;
            socklen_t addr_len = sizeof(client_addr);
            getpeername(client_sock, (sockaddr*)&client_addr, &addr_len);

            NetworkClientInfo* client_info = new NetworkClientInfo{client_sock, client_addr};
            std::lock_guard<std::mutex> lock(ServerClientsMutex);
            ServerClients.push_back(client_info);

            std::thread* listen_thread = new std::thread(&NetworkServer::ListenThreadFunction, this, client_info);
            listen_thread->detach();
        }
    }

    int accept_select(int sockfd) {
        fd_set readfds;
        FD_ZERO(&readfds);
        FD_SET(sockfd, &readfds);

        struct timeval timeout;
        timeout.tv_sec = 1;
        timeout.tv_usec = 0;

        int activity = select(sockfd + 1, &readfds, NULL, NULL, &timeout);
        if (activity < 0) {
            std::cerr << "Select error" << std::endl;
            return -1;
        } else if (activity == 0) {
            return -1;
        }

        if (FD_ISSET(sockfd, &readfds)) {
            return accept(sockfd, NULL, NULL);
        }

        return -1;
    }

    unsigned short GetPort() {
        return port_num;
    }

private:
    std::string host;
    unsigned short port_num;
    bool server_online;
    bool server_listening;
    std::vector<NetworkClientInfo*> ServerClients;
    std::mutex ServerClientsMutex;
    SOCKET server_sock[MAXSOCK];
    int socket_count;
    std::thread* ConnectionThread[MAXSOCK];

