    void ListenThreadFunction(NetworkClientInfo* client_info) {
        char buffer[1024];
        while (server_online) {
            int valread = read(client_info->socket, buffer, sizeof(buffer));
            if (valread <= 0) {
                std::lock_guard<std::mutex> lock(ServerClientsMutex);
                auto it = std::find(ServerClients.begin(), ServerClients.end(), client_info);
                if (it != ServerClients.end()) {
                    close(client_info->socket);
                    delete client_info;
                    ServerClients.erase(it);
                }
                break;
            }
            // Handle received data
        }
    }
