        StopServer();
    }

    void StartServer() {
        server_online = true;
        server_listening = true;

        for (int i = 0; i < MAXSOCK && server_online; ++i) {
            server_sock[i] = socket(AF_INET, SOCK_STREAM, 0);
            if (server_sock[i] < 0) {
                perror("Socket creation failed");
                continue;
            }

            int opt = 1;
            if (setsockopt(server_sock[i], SOL_SOCKET, SO_REUSEADDR | SO_REUSEPORT, &opt, sizeof(opt))) {
                perror("Setsockopt failed");
                close(server_sock[i]);
                continue;
            }

            sockaddr_in server_address;
            memset(&server_address, 0, sizeof(server_address));
            server_address.sin_family = AF_INET;
            server_address.sin_addr.s_addr = inet_addr(host.c_str());
            server_address.sin_port = htons(port_num);

            if (bind(server_sock[i], (sockaddr*)&server_address, sizeof(server_address)) < 0) {
                perror("Bind failed");
