        fd_set readfds;
        FD_ZERO(&readfds);
        FD_SET(sockfd, &readfds);

        struct timeval timeout;
        timeout.tv_sec = 1;
        timeout.tv_usec = 0;

        int activity = select(sockfd + 1, &readfds, NULL, NULL, &timeout);
        if (activity < 0) {
            perror("Select failed");
            return -1;
        } else if (activity == 0) {
            return -1;
        }

        if (FD_ISSET(sockfd, &readfds)) {
            sockaddr_in client_address;
            socklen_t client_len = sizeof(client_address);
            int client_socket = accept(sockfd, (sockaddr*)&client_address, &client_len);
            if (client_socket < 0) {
                perror("Accept failed");
                return -1;
            }
            return client_socket;
        }
        return -1;
    }

    unsigned short GetPort() {
        return port_num;
    }

    void ClientInfoChanged() {
        // Implementation for client info change callback
    }

    void ServerListeningChanged() {
        // Implementation for server listening state change callback
    }

    void ListenThreadFunction(NetworkClientInfo* client_info) {
        char buffer[1024] = {0};
        while (server_online) {
            int valread = read(client_info->client_socket, buffer, 1024);
            if (valread <= 0) {
                if (valread == 0) {
                    std::cout << "Client disconnected" << std::endl;
                } else {
                    perror("Read failed");
                }
                close(client_info->client_socket);
                {
                    std::lock_guard<std::mutex> lock(ServerClientsMutex);
                    auto it = std::find(ServerClients.begin(), ServerClients.end(), client_info);
                    if (it != ServerClients.end()) {
                        ServerClients.erase(it);
                    }
                }
                delete client_info;
                break;
            }
            std::cout << "Received: " << buffer << std::endl;
            send(client_info->client_socket, buffer, strlen(buffer), 0);
            memset(buffer, 0, sizeof(buffer));
        }
    }
};

