        statsThreadRunning_ = false;
        if (stats_thread_.joinable()) {
            stats_thread_.join();
        }
        close(sockfd_);
        unlink(stats_socket_path_.c_str());
    }

    void runSocket() {
        while (statsThreadRunning_) {
            struct sockaddr_un cli_addr;
            socklen_t clilen = sizeof(cli_addr);
            int newsockfd = accept(sockfd_, (struct sockaddr*)&cli_addr, &clilen);
            if (newsockfd >= 0) {
                std::lock_guard<std::mutex> lock(thread_mutex_);
                ++thread_count_;
            }
            if (newsockfd < 0) continue;
            std::thread([this, newsockfd]() {
                processMsg(newsockfd);
                close(newsockfd);
                std::lock_guard<std::mutex> lock(thread_mutex_);
                --thread_count_;
                if (thread_count_ == 0) {
                    thread_exited_.notify_all();
                }
            }).detach();
        }
    }

    void processMsg(int sockfd) {
        char buffer[256];
        int n = read(sockfd, buffer, sizeof(buffer) - 1);
        if (n <= 0) return;
        buffer[n] = '\0';
        std::string msg(buffer);
        std::string response;
        if (msg == "get") {
            std::lock_guard<std::mutex> lock(stats_mutex_);
            for (const auto& [key, value] : stats_) {
                response += key + "=" + std::to_string(value) + ";";
            }
        } else if (msg == "reset") {
            reset();
            response = "reset";
        } else if (msg.substr(0, 8) == "increment ") {
            size_t pos = msg.find(' ', 8);
            if (pos != std::string::npos) {
                std::string key = msg.substr(8, pos - 8);
                int val = std::stoi(msg.substr(pos + 1));
                increment(key, val);
                response = "incremented";
            }
        } else if (msg.substr(0, 4) == "set ") {
            size_t pos = msg.find(' ', 4);
            if (pos != std::string::npos) {
                std::string key = msg.substr(4, pos - 4);
                int val = std::stoi(msg.substr(pos + 1));
                set(key, val);
                response = "set";
            }
        }
        write(sockfd, response.c_str(), response.size());
    }

