        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_[key] = val;
        return stats_[key];
    }

    int reset() {
        std::lock_guard<std::mutex> lock(stats_mutex_);
        stats_.clear();
        return 0;
    }

