typedef websocketpp::server<websocketpp::config::asio> server;

class print_server {
public:
    print_server() : m_next_sessionid(0) {
        m_server.init_asio();
        m_server.set_open_handler(bind(&print_server::on_open,this,_1));
        m_server.set_close_handler(bind(&print_server::on_close,this,_1));
        m_server.set_message_handler(bind(&print_server::on_message,this,_1,_2));
    }

    void on_open(connection_hdl hdl) {
        std::lock_guard<std::mutex> guard(m_mutex);
        m_sessions[hdl] = {m_next_sessionid++, ""};
        std::cout << "Connection opened with session ID " << m_sessions[hdl].session_id << std::endl;
    }

    void on_close(connection_hdl hdl) {
        std::lock_guard<std::mutex> guard(m_mutex);
        auto it = m_sessions.find(hdl);
        if (it != m_sessions.end()) {
            std::cout << "Connection closed for session ID " << it->second.session_id;
            if (!it->second.name.empty()) {
                std::cout << " (" << it->second.name << ")";
            }
            std::cout << std::endl;
            m_sessions.erase(it);
        }
    }

    void on_message(connection_hdl hdl, server::message_ptr msg) {
        std::lock_guard<std::mutex> guard(m_mutex);
        auto it = m_sessions.find(hdl);
        if (it != m_sessions.end()) {
            if (it->second.name.empty()) {
                it->second.name = msg->get_payload();
                std::cout << "Session ID " << it->second.session_id << " identified as " << it->second.name << std::endl;
            } else {
                std::cout << "Message from " << it->second.name << " (session ID " << it->second.session_id << "): " << msg->get_payload() << std::endl;
            }
        }
    }

    void run(uint16_t port) {
        m_server.listen(port);
        m_server.start_accept();
        m_server.run();
    }

private:
    struct session_data {
        int session_id;
        std::string name;
    };

    int m_next_sessionid;
    server m_server;
    std::map<connection_hdl, session_data, std::owner_less<connection_hdl>> m_sessions;
    std::mutex m_mutex;
};

